import React, { useState, useEffect } from "react";
import styled from "styled-components";
import { Button } from "react-bootstrap";

const DayBtn = styled(Button)`
  margin-bottom: 20px;
  & + & {
    margin-left: 10px;
  }
`;

function MainDayBtn({ day1, day7, day30, onDayBtn }) {
  return (
    <>
      <DayBtn day1={day1} on={onDayBtn}>
        최근 1일
      </DayBtn>
      <DayBtn day7={day7} onClick={onDayBtn}>
        최근 7일
      </DayBtn>
      <DayBtn day30={day30} onClick={onDayBtn}>
        최근 30일
      </DayBtn>
    </>
  );
}

export default MainDayBtn;
