import React, { useState, useEffect } from "react";
import * as tagData from "../lib/api/getData";
import TrendContainer from "../component/mainTrendContainer";
import styled from "styled-components";
import { Button } from "react-bootstrap";

const CategoryBox = styled.div`
  text-align: center;
`;

const DayBtn = styled(Button)`
  margin-bottom: 10px;
  & + & {
    margin-left: 10px;
  }
`;

function Main() {
  const [trendData, setTrendData] = useState(tagData.mainData);

  const [day, setDay] = useState({
    gap: "day1_gap",
    rate: "day1_rate",
  });

  useEffect(async () => {
    //day1_gap
    const getTrendData = await tagData.mainData;

    setTrendData(getTrendData);

    console.log("trendData", trendData);
  }, []);
  return (
    <>
      <CategoryBox>
        <h2>포스팅 증가량 순위</h2>
        <DayBtn>최근 1일</DayBtn>
        <DayBtn>최근 7일</DayBtn>
        <DayBtn>최근 30일</DayBtn>
        <TrendContainer type="gap" data={trendData} day={day.gap} />
      </CategoryBox>

      <CategoryBox>
        <h2>포스팅 증가율 순위</h2>
        <DayBtn>최근 1일</DayBtn>
        <DayBtn>최근 7일</DayBtn>
        <DayBtn>최근 30일</DayBtn>
        <TrendContainer type="rate" data={trendData} day={day.gap} />
      </CategoryBox>

      <CategoryBox>
        <h2>새롭게 분석 대상이 된 해시태그</h2>
        <TrendContainer type="recent" data={trendData} day="recent_list" />
      </CategoryBox>
    </>
  );
}

export default Main;
